       private void loginCheck(){
        if(cboxRole.getSelectedItem().toString().equals("Choose a category") || txtUsername.getText().equals("") || txtPassword.getText().equals("")){
            JOptionPane.showMessageDialog(rootPane, 
                                          "Data cannot be empty", 
                                          "Message", 
                                          JOptionPane.ERROR_MESSAGE);
                txtUsername.requestFocus();
        }else{
            JOptionPane.showMessageDialog(rootPane,
                                               "The username or password are incorrect\n"
                                             + "or the account has not been registered", 
                                          "Pesan", 
                                          JOptionPane.ERROR_MESSAGE );
        }
    }
       
