    private void btnSubmitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubmitActionPerformed

    if(cboxRole.getSelectedItem().toString().equals("Choose a category")){
    JOptionPane.showMessageDialog(rootPane, 
                                       "Data cannot be empty", 
                                       "Message", 
                                       JOptionPane.ERROR_MESSAGE);
            txtUsername.requestFocus();
    }
     else if (txtUsername.getText().equals("") ||txtPassword.getText().equals("")){
         JOptionPane.showMessageDialog(rootPane, 
                                       "Data cannot be empty", 
                                       "Message", 
                                       JOptionPane.ERROR_MESSAGE);
            txtUsername.requestFocus();
    }
    else {
            String role     = cboxRole.getSelectedItem().toString();
            String username = txtUsername.getText();
            String password = txtPassword.getText();
        User user = uc.getLogin(role, username, password);
        if(user != null){
                ConstUtil.auth = user;
                this.setVisible(false);
            
            switch (cboxRole.getSelectedItem().toString()) {
            case "Mahasiswa":
                new mainViewMhs().setVisible(true);
                break;
            case "Dosen":
                new mainViewDosen().setVisible(true);
                break;
            default:
                new mainViewKeasramaan().setVisible(true);
                break;
            }
     
                }
        else{
            JOptionPane.showMessageDialog(rootPane, 
                    "The username and password are incorrect or the account has not been registered", 
                    "Pesan", 
                    JOptionPane.ERROR_MESSAGE);
            }
        }

    }//GEN-LAST:event_btnSubmitActionPerformed

